const leads = $node['enrich_processor_008'].json.enriched_leads;

const scoredLeads = leads.map(lead => {
  let score = 0;
  let factors = [];

  // Urgency factor (25 points)
  if (lead.urgency === 'high') {
    score += 25;
    factors.push('High urgency (+25)');
  } else if (lead.urgency === 'medium') {
    score += 15;
    factors.push('Medium urgency (+15)');
  }

  // Commitment factor (20 points)
  if (lead.commitment_level === 'ready_to_start') {
    score += 20;
    factors.push('Ready to start (+20)');
  } else if (lead.commitment_level === 'interested') {
    score += 10;
    factors.push('Interested (+10)');
  }

  // Income goal factor (15 points)
  const incomeGoal = parseInt(lead.income_goal);
  if (incomeGoal >= 10000) {
    score += 15;
    factors.push('High income goal (+15)');
  } else if (incomeGoal >= 5000) {
    score += 10;
    factors.push('Medium income goal (+10)');
  }

  // Experience factor (10 points)
  if (lead.experience === 'experienced') {
    score += 10;
    factors.push('Experienced (+10)');
  } else if (lead.experience === 'some') {
    score += 5;
    factors.push('Some experience (+5)');
  }

  // Enrichment quality (15 points)
  if (lead.enrichment_score >= 75) {
    score += 15;
    factors.push('High data quality (+15)');
  } else if (lead.enrichment_score >= 50) {
    score += 10;
    factors.push('Good data quality (+10)');
  }

  // Social presence (10 points)
  if (lead.social_profiles.linkedin) {
    score += 5;
    factors.push('LinkedIn profile (+5)');
  }
  if (lead.company) {
    score += 5;
    factors.push('Company identified (+5)');
  }

  // Dream car indicator (5 points)
  if (lead.dream_car?.includes('Porsche') || lead.dream_car?.includes('Tesla')) {
    score += 5;
    factors.push('Premium dream car (+5)');
  }

  // Calculate grade
  let grade;
  if (score >= 90) grade = 'A+';
  else if (score >= 80) grade = 'A';
  else if (score >= 70) grade = 'B';
  else if (score >= 60) grade = 'C';
  else if (score >= 50) grade = 'D';
  else grade = 'F';

  // Determine priority
  let priority;
  if (score >= 80) priority = 'IMMEDIATE';
  else if (score >= 60) priority = 'HIGH';
  else if (score >= 40) priority = 'MEDIUM';
  else priority = 'LOW';

  return {
    lead_id: lead.id,
    lead_name: lead.full_name,
    final_score: score,
    max_score: 100,
    percentage: score + '%',
    grade: grade,
    priority: priority,
    scoring_factors: factors,

    recommendations: {
      avatar: score >= 80 ? 'Crystal_Lion' : score >= 60 ? 'Mathias' : 'Lina',
      approach: score >= 80 ? 'direct_close' : score >= 60 ? 'consultative' : 'nurturing',
      follow_up: score >= 80 ? 'immediate' : score >= 60 ? 'within_24h' : 'within_week',
      offer: score >= 80 ? 'premium_package' : score >= 60 ? 'standard' : 'starter'
    }
  };
});

