// ADVANCED ERROR HANDLER WITH EXPONENTIAL BACKOFF
const maxRetries = 3;
const baseDelay = 1000;
const errors = [];

const fallbacks = {
  'flux': ['leonardo', 'ideogram'],
  'kling': ['minimax', 'runway'],
  'heygen': ['elevenlabs'],
  'claude': ['deepseek'],
  'runway': ['luma'],
  'blotato': ['predis', 'klap']
};

function exponentialBackoff(attempt) {
  return baseDelay * Math.pow(2, attempt - 1) + Math.random() * 1000;
}

function handleError(error, nodeName, attempt) {
  const errorLog = {
    timestamp: new Date().toISOString(),
    node: nodeName,
    error: error?.message || 'Unknown error',
    code: error?.code || error?.status || 'UNKNOWN',
    attempt: attempt || 1,
    severity: 'HIGH'
  };

  if (error?.status === 429) {
    errorLog.retry_after = exponentialBackoff(errorLog.attempt);
    errorLog.action = 'RETRY_WITH_BACKOFF';
    errorLog.severity = 'MEDIUM';
  } else if (error?.status >= 500) {
    errorLog.action = 'RETRY_WITH_FALLBACK';
    errorLog.fallback_options = fallbacks[nodeName.toLowerCase()] || [];
  } else if (errorLog.attempt >= maxRetries) {
    errorLog.action = 'FINAL_FALLBACK';
    errorLog.severity = 'CRITICAL';
  } else {
    errorLog.action = 'RETRY';
  }

  return errorLog;
}

const nodeList = Object.keys($node);
nodeList.forEach(nodeName => {
  if ($node[nodeName]?.error) {
    errors.push(handleError($node[nodeName].error, nodeName, $execution.retryOf || 1));
  }
});

const systemHealth = {
  total_nodes: 45,
  successful_nodes: 45 - errors.length,
  error_rate: ((errors.length / 45) * 100).toFixed(2) + '%',
  health_status: errors.length === 0 ? 'EXCELLENT' : errors.length < 3 ? 'GOOD' : 'NEEDS_ATTENTION'
};

return {
  error_handling: errors.length > 0 ? errors : 'No errors - System running perfectly!',
  system_health: systemHealth,
  max_retries: maxRetries,
  backoff_strategy: 'exponential_with_jitter',
  fallback_ready: true,
  recovery_options: fallbacks
};

