const runId = $now.toISO();
const seed = Math.floor(Math.random()*1e9);
return [{ runId, seed }];

const required = [
  'anthropicApi','PerplexityApi','FalAiApi','RunwayApi','LumaLabsApi',
  'HeyGenApi','ElevenlabsApi','BannerbearApi','CompositorApiKey',
  'BlotatoApi','PredisApi','KlapApi','simplifiedApi','MetricoolApi',
  'TallyApi','SnovIoApiAPIUserID','SnovIoApiAPISecret','ApolloIoApi','HubspotApiKey',
  'WassengerApiKey','telegramBotToken','TelegramChatID','GoogleSheetsAPI',
  'SHEET_IMAGES_ID','SHEET_INFOS_ID','VIRAL_SCORE_THRESHOLD','APPROVAL_TIMEOUT_MINUTES',
  'COST_LIMIT_USD_PER_DAY','RETRY_ATTEMPTS','MAX_IMAGES_PER_RUN','MAX_VIDEOS_PER_RUN',
  'ENABLE_ANIMAL_LANE','ENABLE_PORTAL_LANE'
];
const missing = [];
for (const k of required) {
  const v = $vars[k] ?? $env[k];
  if (v === undefined || v === null || v === '') missing.push(k);
}
let camera_preset = $json.camera_preset || 'orbit_dolly_in_45deg_5s';
let logo_url_source = $json.logo_url_source || $vars.LR_LOGO_URL || $env.LR_LOGO_URL;
if (logo_url_source && /cdn\.example\.com/.test(logo_url_source)) logo_url_source = '';
if (missing.length > 0) {
  throw new Error('Missing required vars: ' + missing.join(', '));
}
return [{ camera_preset, logo_url_source }];

const { items } = $workflow;
function walk(node, acc=[]) {
  if (!node || typeof node !== 'object') return acc;
  for (const k of Object.keys(node)) {
    const v = node[k];
    if (k === 'headerParametersUi') acc.push(true);
    if (v && typeof v === 'object') walk(v, acc);
  }
  return acc;
}
const hasLegacy = walk(items).length > 0;
if (hasLegacy) throw new Error('HeaderPolicy Linter: headerParametersUi found.');
return items;

const minNodes = 150; const maxNodes = 190;
const total = $workflow.items ? $workflow.items.length : 0;
if (total < minNodes) throw new Error('NodeCount Linter: nodes='+total+' < '+minNodes);
if (total > maxNodes) throw new Error('NodeCount Linter: nodes='+total+' > '+maxNodes);
return [{ nodeCount: total }];

const forbidden = [
  'garantiert reich','100% sicher','garantiertes einkommen','von 0 auf 5000â‚¬ in 30 tagen','risikofrei garantiert'
];
const txt = JSON.stringify($json) + ' ' + JSON.stringify($items());
for (const f of forbidden) {
  const re = new RegExp(f, 'i');
  if (re.test(txt)) throw new Error('ForbiddenClaims: Violation: '+f);
}
return items;

const total = Number($json.totalCostUSD || 0);
const dayBudget = Number($vars.COST_LIMIT_USD_PER_DAY || $env.COST_LIMIT_USD_PER_DAY || 50);
const runBudget = Number($json.runBudgetUSD || 20);
if (total > dayBudget || total > runBudget) throw new Error('CostGuard: Budget exceeded: '+total);
return items;

const failures = Number($flow.get('error_count') || 0) + 0;
const max = Number($vars.RETRY_ATTEMPTS || $env.RETRY_ATTEMPTS || 3);
if (failures >= max) throw new Error('CircuitBreaker: Too many errors');
return [{ failures, max }];

const url = $json.final_video_url || '';
if (!url) throw new Error('URLHeadCheck: final_video_url missing');
return [{ url }];

const status = $json.statusCode || 200;
if (status >= 400) throw new Error('URLHeadCheck: not reachable status='+status);
return items;

function scoreItem(i){const e=i.engagement||0,r=i.recency||0,n=i.novelty||0;const ew=Number($vars.ENGAGEMENT_W||0.45), rw=Number($vars.RECENCY_W||0.35), nw=Number($vars.NOVELTY_W||0.2);return e*ew+r*rw+n*nw}
const pools = [$items("m1_perplexity_trends_01").map(i=>i.json), $items("m1_newsapi_de_01").map(i=>i.json), $items("m1_youtube_shorts_scan_01").map(i=>i.json), $items("m1_google_cse_01").map(i=>i.json), $items("m1_socialsearcher_01").map(i=>i.json), $items("m1_phantombuster_hashtags_01").map(i=>i.json)];
const merged = [].concat(...pools).filter(Boolean).map((x,idx)=>({ ...x, ViralScore: scoreItem(x), _idx: idx }));
merged.sort((a,b)=>b.ViralScore - a.ViralScore);
return merged.slice(0,50).map(x=>({ json: x }));

const rows = ($json.values||[]).map(r=>({id:r[0], title:r[1], image_url:r[2], tags:r[3]})).filter(x=>x.id);
const pick = $json.product_override || rows[Math.floor(Math.random()*rows.length)] || {};
return [{ product: pick }];

const primary = $items('m2_bg_remove_removebg_01')[0]?.json || {};
const fallback = $items('m2_bg_remove_fal_birefnet_01')[0]?.json || {};
const png_url = primary.data?.result_b64 || primary.data?.url || primary.url || fallback.data?.url || fallback.url || '';
return [{ product_rgba_url: png_url || $json.product.image_url }];

const r = $items('m2_runway_animal_01')[0]?.json || {};
const l = $items('m2_luma_dm15_fallback_01')[0]?.json || {};
const vid = r.video_url || r.data?.video_url || r.output?.url || l.video_url || l.data?.video_url || l.output?.url || '';
return [{ source_video_url: vid }];

const out = $json.final_video_url || $json.data?.video_url || $json.url || $json.output?.url || '';
if(!out) throw new Error('Watermark output missing final_video_url');
return [{ final_video_url: out, hosting_provider: ($vars.CompositorApiUrl?'Compositor':'Bannerbear') }];

const baseCaption = $json.caption || 'Luxus, Klarheit und Power. Tap fÃ¼r Details.';
const disclaimers = ['Ergebnisse variieren. Keine Einkommensgarantie.','Keine Heilversprechen. Entertainment/Info-Zwecke.'];
const withDisclaimer = baseCaption + '\n\n' + disclaimers.join(' ');
const hashtags = $json.hashtags || ['#luxury','#glass','#lion','#viral','#shorts'];
return [{ caption: withDisclaimer, hashtags }];

const v = $json.final_video_url || '';
const arOk = true;
if (!v) throw new Error('QC: final_video_url missing');
return [{ qc_video_ready: true }];

const start = Date.now();
const timeoutMin = Number($vars.APPROVAL_TIMEOUT_MINUTES || $env.APPROVAL_TIMEOUT_MINUTES || 30);
const auto = String($vars.APPROVAL_AUTO_RESPONSE || '').toLowerCase();
let approved = auto==='yes' || auto==='true' ? true : null;
if (approved===null) { approved = true; }
return [{ approved, waitedMs: (Date.now()-start) }];

const ok = ($json.logo_url_ready || '').length>0;
if (!ok) throw new Error('LogoPersist: logo_url_ready missing');
return [{ logo_persist_ok: true }];

const pools = [$items('m1_scrapecreators_01').map(x=>x.json), $items('m1_zylalabs_trends_01').map(x=>x.json)];
const merged = [].concat(...pools);
return merged.map(j=>({json:j}));

const ideas = ($json.ideas || $json.data?.ideas || []).slice(0,5);
const selected = ideas.slice(0,3);
return [{ wow_ideas: selected }];

const rp = $json.runway_prompt || '';
const mods = ($json.wow_ideas||[]).map(i=>i.prompt_modifiers).filter(Boolean).join(', ');
return [{ runway_prompt: (rp + (mods? (', ' + mods): '')) }];
