function calculateAvatarMatch(lead, avatar) {
  let score = 0;

  // Interest-based matching
  const interests = lead.interests || lead.q_keywords || '';

  if (interests.includes('luxury') || interests.includes('car')) {
    if (avatar.specialty.includes('Traumauto')) score += 30;
  }

  if (interests.includes('freedom') || interests.includes('financial')) {
    if (avatar.specialty.includes('Freiheit')) score += 30;
  }

  if (interests.includes('passive') || interests.includes('income')) {
    if (avatar.specialty.includes('Passive Income')) score += 30;
  }

  if (interests.includes('team') || interests.includes('community')) {
    if (avatar.specialty.includes('Team') || avatar.specialty.includes('Community')) score += 25;
  }

  // Pain point matching
  const painPoints = lead.pain_points || lead.challenges || '';

  if (painPoints.includes('fear') || painPoints.includes('angst')) {
    if (avatar.name === 'Lina' || avatar.specialty.includes('Angst')) score += 40;
  }

  if (painPoints.includes('motivation') || painPoints.includes('vision')) {
    if (avatar.name === 'Mathias' || avatar.specialty.includes('Motivation')) score += 40;
  }

  // Goal matching
  const goals = lead.goals || lead.objectives || '';

  if (goals.includes('wealth') || goals.includes('rich')) {
    if (avatar.specialty.includes('Wealth')) score += 35;
  }

  if (goals.includes('success') || goals.includes('achieve')) {
    if (avatar.specialty.includes('Success') || avatar.specialty.includes('Achiever')) score += 35;
  }

  // Random quantum factor for variety
  score += Math.random() * 10;

  return score;
}

const avatarAssignments = [];

leads.forEach(lead => {
  // Calculate scores for all avatars
  const scores = avatarProfiles.map(avatar => ({
    avatar: avatar,
    score: calculateAvatarMatch(lead.json, avatar)
  }));

  // Sort by score and pick the best match
  scores.sort((a, b) => b.score - a.score);
  const bestMatch = scores[0];

  // Determine emotion based on lead profile
  let emotion = 'enthusiastic'; // default

  if (lead.json.pain_points?.includes('fear')) {
