// GENETIC ALGORITHM PROMPT OPTIMIZER FROM YEAR 3025
const promptHistory = $node['read_prompt_dna_010'].json || [];
const currentPerformance = $node['performance_analyzer_009'].json;

class GeneticPromptOptimizer {
  constructor(population, performance) {
    this.population = population;
    this.performance = performance;
    this.mutationRate = 0.05;
    this.crossoverRate = 0.7;
    this.eliteSize = 10;
  }

  // Calculate fitness score for each prompt
  calculateFitness(prompt) {
    let fitness = 0;

    // Check for high-performing keywords
    const powerWords = ['crystal', 'l√∂we', 'glass', 'quantum', '99‚Ç¨', 'traumauto', 'portal', 'hologram'];
    powerWords.forEach(word => {
      if (prompt.toLowerCase().includes(word)) fitness += 10;
    });

    // Bonus for emotional triggers
    const emotionWords = ['wow', 'unm√∂glich', 'galaxy', 'milliarde', 'freiheit'];
    emotionWords.forEach(word => {
      if (prompt.toLowerCase().includes(word)) fitness += 5;
    });

    // Length optimization (shorter is better for hooks)
    if (prompt.length < 100) fitness += 20;

    // Viral score from history
    const historicalScore = this.getHistoricalScore(prompt);
    fitness += historicalScore;

    return fitness;
  }

  getHistoricalScore(prompt) {
    // Find similar prompts in history and their performance
    const similar = promptHistory.filter(p => 
      this.similarity(p.prompt, prompt) > 0.7
    );

    if (similar.length > 0) {
      const avgScore = similar.reduce((acc, p) => 
        acc + parseFloat(p.viral_score || 0), 0
      ) / similar.length;
      return avgScore;
    }
    return 50; // Default score
  }

  similarity(str1, str2) {
    // Simple similarity calculation
    const words1 = str1.toLowerCase().split(' ');
    const words2 = str2.toLowerCase().split(' ');
    const intersection = words1.filter(w => words2.includes(w));
    return intersection.length / Math.max(words1.length, words2.length);
  }

  // Crossover two parent prompts
  crossover(parent1, parent2) {
    if (Math.random() > this.crossoverRate) {
      return [parent1, parent2];
    }

    const words1 = parent1.split(' ');
    const words2 = parent2.split(' ');
    const crossPoint = Math.floor(Math.random() * Math.min(words1.length, words2.length));

    const child1 = [...words1.slice(0, crossPoint), ...words2.slice(crossPoint)].join(' ');
    const child2 = [...words2.slice(0, crossPoint), ...words1.slice(crossPoint)].join(' ');

    return [child1, child2];
  }

  // Mutate a prompt
  mutate(prompt) {
    if (Math.random() > this.mutationRate) {
      return prompt;
    }

    const mutations = [
      'Crystal-L√∂we EXPLODIERT: ',
      'QUANTUM-PORTAL √∂ffnet sich: ',
      '99‚Ç¨ = MILLION√ÑR: ',
      'GLASS-DNA aktiviert: ',
      'VSMR-HYPNOSE beginnt: ',
      '3D-HOLOGRAMM zeigt: ',
      'UNM√ñGLICH aber REAL: ',
      'GALAXY-DOMINATION durch: '
    ];

    const randomMutation = mutations[Math.floor(Math.random() * mutations.length)];
    return randomMutation + prompt;
  }

  // Generate next generation of prompts
  evolve() {
    const nextGeneration = [];

    // Elite selection (keep best performers)
    const sortedByFitness = this.population
      .map(p => ({ prompt: p, fitness: this.calculateFitness(p) }))
      .sort((a, b) => b.fitness - a.fitness);

    for (let i = 0; i < this.eliteSize && i < sortedByFitness.length; i++) {
      nextGeneration.push(sortedByFitness[i].prompt);
    }

    // Generate rest through crossover and mutation
    while (nextGeneration.length < 20) {
      const parent1 = this.tournamentSelection(sortedByFitness);
      const parent2 = this.tournamentSelection(sortedByFitness);

      const [child1, child2] = this.crossover(parent1.prompt, parent2.prompt);

      nextGeneration.push(this.mutate(child1));
      if (nextGeneration.length < 20) {
        nextGeneration.push(this.mutate(child2));
      }
    }

    return nextGeneration;
  }

  tournamentSelection(population, tournamentSize = 3) {
    const tournament = [];
    for (let i = 0; i < tournamentSize; i++) {
      tournament.push(population[Math.floor(Math.random() * population.length)]);
    }
    return tournament.sort((a, b) => b.fitness - a.fitness)[0];
  }
}

