  negative_mentions: 0,
  neutral_mentions: 0,

  // AI Insights
  patterns_detected: [],
  optimization_opportunities: [],
  predicted_next_viral: null
};

// Process Metricool data
if (allAnalytics[0]?.json?.data) {
  const metricool = allAnalytics[0].json.data;
  performanceMetrics.total_reach += metricool.reach || 0;
  performanceMetrics.total_impressions += metricool.impressions || 0;
  performanceMetrics.total_engagement += metricool.engagement || 0;
}

// Process GA4 data
if (allAnalytics[1]?.json?.rows) {
  const ga4 = allAnalytics[1].json.rows;
  ga4.forEach(row => {
    performanceMetrics.total_views += parseInt(row.metricValues?.[0]?.value || 0);
    performanceMetrics.total_conversions += parseInt(row.metricValues?.[3]?.value || 0);
  });
}

// Process HubSpot data
if (allAnalytics[2]?.json?.results) {
  const hubspot = allAnalytics[2].json.results;
  performanceMetrics.new_leads = hubspot.length;
  performanceMetrics.qualified_leads = hubspot.filter(c => 
    c.properties?.lifecyclestage === 'qualifiedlead'
  ).length;
  performanceMetrics.team_members_recruited = hubspot.filter(c => 
    c.properties?.lr_team_member === 'true'
  ).length;
}

// Process Social Mentions
if (allAnalytics[3]?.json?.posts) {
  const mentions = allAnalytics[3].json.posts;
  mentions.forEach(post => {
    if (post.sentiment === 'positive') performanceMetrics.positive_mentions++;
    else if (post.sentiment === 'negative') performanceMetrics.negative_mentions++;
    else performanceMetrics.neutral_mentions++;
  });

  // Calculate sentiment score
  const total_mentions = performanceMetrics.positive_mentions + 
                         performanceMetrics.negative_mentions + 
                         performanceMetrics.neutral_mentions;
  if (total_mentions > 0) {
    performanceMetrics.sentiment_score = 
      ((performanceMetrics.positive_mentions - performanceMetrics.negative_mentions) / total_mentions) * 100;
  }
}

// Process YouTube stats
if (allAnalytics[4]?.json?.items) {
  const youtube = allAnalytics[4].json.items;
  youtube.forEach(video => {
    const stats = video.statistics;
    performanceMetrics.platform_performance.youtube.views += parseInt(stats?.viewCount || 0);

    // Identify viral content (>1M views)
    if (parseInt(stats?.viewCount || 0) > 1000000) {
      performanceMetrics.viral_content.push({
        title: video.snippet?.title,
        views: stats?.viewCount,
        likes: stats?.likeCount,
        engagement_rate: (parseInt(stats?.likeCount || 0) + parseInt(stats?.commentCount || 0)) / parseInt(stats?.viewCount || 1)
      });
    }
  });
}

// Calculate conversion rate
if (performanceMetrics.total_views > 0) {
  performanceMetrics.conversion_rate = 
    (performanceMetrics.total_conversions / performanceMetrics.total_views) * 100;
}

// Calculate average engagement rate
if (performanceMetrics.total_impressions > 0) {
  performanceMetrics.average_engagement_rate = 
    (performanceMetrics.total_engagement / performanceMetrics.total_impressions) * 100;
}

// Detect patterns using AI logic
if (performanceMetrics.average_engagement_rate > 15) {
  performanceMetrics.patterns_detected.push('High engagement detected - Crystal Lion content resonating');
