    screenshot_moments: 2
  }
};

// Calculate enhanced viral score
const baseScore = contentData.avgViralScore || 75;
const patternBonus = viralPatterns.baby_shark_elements.repetition_score * 0.1;
const engagementBonus = Object.values(viralPatterns.engagement_triggers).filter(Boolean).length * 2;
const multiplierBonus = viralPatterns.viral_multipliers.share_bait_moments * 3;

const enhancedViralScore = Math.min(100, baseScore + patternBonus + engagementBonus + multiplierBonus);

return [{
  json: {
    ...contentData,
    viral_patterns: viralPatterns,
    enhanced_viral_score: enhancedViralScore,
    viral_prediction: enhancedViralScore > 90 ? 'high_viral_potential' : 'moderate_viral_potential',
