// DNA-Feedback: Gewinner-Prompts hochstufen
const items = $input.all();
const analyticsData = items[0].json;

// Analyze performance patterns
const winners = analyticsData.posts?.filter(post => post.engagement_rate > 5) || [];
const topPrompts = winners.map(post => post.prompt_used).slice(0, 5);
const topCameraMoves = winners.map(post => post.camera_move).slice(0, 3);

// Update scoring weights based on performance
const updatedWeights = {
  animal_lane_weight: winners.filter(p => p.lane === 'animal').length > 0 ? 1.2 : 1.0,
  portal_lane_weight: winners.filter(p => p.lane === 'portal').length > 0 ? 1.1 : 1.0,
  asmr_weight: winners.filter(p => p.has_asmr).length > 0 ? 1.15 : 1.0,
  impossible_moment_weight: winners.filter(p => p.has_impossible_moment).length > 0 ? 1.25 : 1.0
};

return [{
  json: {
    dna_feedback_complete: true,
    top_prompts: topPrompts,
    top_camera_moves: topCameraMoves,
    updated_weights: updatedWeights,
    winner_count: winners.length,
    timestamp: new Date().toISOString()
  }
}];

// CostGuard - Track and limit costs
const items = $input.all();
const currentData = items[0].json;

// Calculate costs for this run
const costs = {
  perplexity: 0.02, // per request
  anthropic: 0.15, // per request
  runway: 2.50, // per video
  luma: 1.80, // per video
  flux: 0.08, // per image
  heygen: 0.30, // per avatar video
  elevenlabs: 0.05, // per audio
  total: 0
};

costs.total = Object.values(costs).reduce((sum, cost) => sum + cost, 0);

// Check daily limit
const dailyLimit = parseFloat($vars.COST_LIMIT_USD_PER_DAY || 100);
const currentTotal = (currentData.totalCost || 0) + costs.total;

if (currentTotal > dailyLimit) {
  throw new Error(`Daily cost limit exceeded: $${currentTotal.toFixed(2)} > $${dailyLimit}`);
}

return [{
  json: {
    ...currentData,
    run_costs: costs,
    totalCost: currentTotal,
    dailyLimit,
    cost_guard_passed: true
  }
}];

// Circuit Breaker - Error handling and backoff
const items = $input.all();
const data = items[0].json;

// Track error counts (in real implementation, this would use persistent storage)
const errorCounts = {
  runway_errors: 0,
  flux_errors: 0,
  heygen_errors: 0,
  total_errors: 0
};

// Check if circuit breaker should trip
const maxErrors = 5;
const shouldTrip = errorCounts.total_errors > maxErrors;

if (shouldTrip) {
  console.log('Circuit breaker tripped - implementing backoff');
  // In real implementation, would implement exponential backoff
}

return [{
  json: {
    ...data,
    circuit_breaker_status: shouldTrip ? 'tripped' : 'closed',
    error_counts: errorCounts,
    backoff_seconds: shouldTrip ? Math.pow(2, errorCounts.total_errors) : 0
  }
}];

// ForbiddenClaims Guard - Block prohibited phrases
const items = $input.all();
const contentData = items[0].json;

// Forbidden phrases/claims
const forbiddenClaims = [
  'garantiert reich',
  '100% sicher',
  'ohne risiko',
  'schnell reich werden',
  'geld ohne arbeit',
  'sofort millionär',
  'risikofreie investition'
];

// Check all text content
const textToCheck = [
  contentData.caption || '',
  contentData.description || '',
  JSON.stringify(contentData.hooks || []),
  JSON.stringify(contentData.story_arcs || [])
].join(' ').toLowerCase();

// Find violations
const violations = forbiddenClaims.filter(claim => 
  textToCheck.includes(claim.toLowerCase())
);

if (violations.length > 0) {
  throw new Error(`Forbidden claims detected: ${violations.join(', ')}`);
}

// Add required disclaimers
const disclaimers = [
  'Erfolg ist nicht garantiert und hängt von individuellen Faktoren ab.',
  'Vergangene Ergebnisse sind keine Garantie für zukünftige Erfolge.',
  'LR Health & Beauty - Made in Germany.'
];

return [{
  json: {
    ...contentData,
    forbidden_claims_check: 'passed',
    violations_found: violations,
    disclaimers_added: disclaimers,
    compliance_verified: true
  }
}];

// LogoPersist Guard - Ensure logo presence
const items = $input.all();
const mediaData = items[0].json;

// Check logo presence in videos (≥95% frames)
const logoPresenceCheck = {
  video_frames_with_logo: 98, // Simulated - would be actual frame analysis
  required_percentage: 95,
  logo_visible_images: true,
  logo_url_accessible: true
};

const logoPresenceOK = logoPresenceCheck.video_frames_with_logo >= logoPresenceCheck.required_percentage;

if (!logoPresenceOK) {
