for (const [category, factors] of Object.entries(engagementFactors)) {
  const categoryScore = Object.values(factors).reduce((sum, score) => sum + score, 0) / Object.values(factors).length;
  predictedEngagement += categoryScore * weights[category];
}

// Engagement tier classification
let engagementTier;
if (predictedEngagement >= 90) engagementTier = 'viral_tier';
else if (predictedEngagement >= 80) engagementTier = 'high_engagement';
else if (predictedEngagement >= 70) engagementTier = 'good_engagement';
else engagementTier = 'moderate_engagement';

